import React, { useContext, useState } from 'react';
import myContext from '../context/myContext';

export default function NumericFilter() {
  const {
    filterByNumericValues,
    setFilterByNumericValues,
  } = useContext(myContext);

  const [column, setColumn] = useState('population');
  const [comparison, setComparison] = useState('maior que');
  const [value, setValue] = useState(0);

  function handleFilterClick() {
    const actualFilter = {
      column,
      comparison,
      value,
    };

    const newFiltersArray = [...filterByNumericValues, actualFilter];

    setFilterByNumericValues(newFiltersArray);
    removeFilter();
  }

  function optionGenerator(option) {
    return (
      <option
        value={ option }
        key={ option }
      >
        {option}
      </option>
    );
  }

  return (
    <div>
      <label htmlFor="columnInput">
        Filtre por coluna:
        <select
          data-testid="column-filter"
          id="columnInput"
          onChange={ ({ target }) => setColumn(target.value) }
        >
          {options.map((option) => optionGenerator(option))}
          <option
            value="population"
          >
            population
          </option>
          <option
            value="orbital_period"
          >
            orbital_period
          </option>
          <option
            value="diameter"
          >
            diameter
          </option>
          <option
            value="rotation_period"
          >
            rotation_period
          </option>
          <option
            value="surface_water"
          >
            surface_water
          </option>
        </select>
      </label>
      <label htmlFor="comparisonInput">
        Maior ou menor ?
        <select
          data-testid="comparison-filter"
          id="comparisonInput"
          onChange={ ({ target }) => setComparison(target.value) }
        >
          <option
            value="maior que"
          >
            maior que
          </option>
          <option
            value="menor que"
          >
            menor que
          </option>
          <option
            value="igual a"
          >
            igual a
          </option>
        </select>
      </label>
      <label htmlFor="valueInput">
        Valor:
        <input
          data-testid="value-filter"
          id="valueInput"
          type="number"
          value={ value }
          onChange={ ({ target }) => setValue(target.value) }
        />
      </label>
      <button
        data-testid="button-filter"
        type="button"
        onClick={ handleFilterClick }
      >
        Filtrar
      </button>
    </div>
  );
}
